import React from 'react';
import {Button} from './Button';
import {Link} from 'react-router-dom';
import './Work.css';

function Work({
    lightBg, topLine, lightText, lightTextDesc, headLine, description, 
    buttonLabel, img, alt, imgStart, workPage
}) {
    return (
        <>
            <div className={lightBg ? 'work-section' : 'work-section darkBg'}>
                <div className="containter">
                    <div className="row work-row" 
                        style={{display: 'flex', flexDirection: imgStart === 'start' ? 'row-reverse' : 'row'}}>
                            <div className="col">
                                <div className="work-text-wrapper">
                                    <Link to={workPage}>
                                        <h1 className={lightText ? 'heading' : 'heading dark'}>
                                            {headLine}
                                        </h1>
                                    </Link>
                                    
                                    <p className={lightTextDesc ? 'work-subtitle' : 'work-subtitle dark'}>
                                      {description}  
                                    </p>
                                </div>
                            </div>
                            <div className="col">
                                <Link to={workPage}>

                                    <div className="work-img-wrapper">
                                        <img src={img} alt={alt} className="work-img">
                                        </img>

                                    </div>
                                </Link>
                                
                            </div>    
                    </div>
                </div>
            </div>
        </>
    )
}

export default Work;
